mapper.select:


    dimc.sync_status = 0
     and dimc.change_type = #{changeType}
     and dimc.operation_type = #{operationType}
     and dimc.object_api = #{objectApi}
     and dimc.field_api = #{fieldApi}
     and dimc.object_label = #{objectLabel}
     and dimc.field_label = #{fieldLabel}
     and dimc.change_time = #{changeTime}
     and dimc.change_user = #{changeUser}
     and dimc.change_reason = #{changeReason}
     and dimc.sync_time = #{syncTime}
     and dimc.sync_error_message = #{syncErrorMessage}
     and dimc.retry_count = #{retryCount}
     and dimc.last_retry_time = #{lastRetryTime}
     and dimc.is_custom = #{isCustom}
     and dimc.dept_id = #{deptId}

order by dimc.change_time desc

mapper.update:
update datai_integration_metadata_change
change_type = #{changeType}, operation_type = #{operationType}, object_api = #{objectApi}, field_api = #{fieldApi}, object_label = #{objectLabel}, field_label = #{fieldLabel}, change_time = #{changeTime}, change_user = #{changeUser}, change_reason = #{changeReason}, sync_status = #{syncStatus}, sync_time = #{syncTime}, sync_error_message = #{syncErrorMessage}, retry_count = #{retryCount}, last_retry_time = #{lastRetryTime}, is_custom = #{isCustom}, dept_id = #{deptId}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark},
where datai_integration_metadata_change.id = #{id}

mapper.select:
select count(*) from datai_integration_metadata_change
where change_type = #{changeType}
  and operation_type = #{operationType}
  and object_api = #{objectApi}

      and field_api = #{fieldApi}


      and field_api is null

  and change_reason = #{changeReason}
  and sync_status = false
order by change_time desc
limit 1

select DATE_FORMAT(change_time, '%Y-%m-%d') as timeKey DATE_FORMAT(change_time, '%Y-%u') as timeKey DATE_FORMAT(change_time, '%Y-%m') as timeKey CONCAT(YEAR(change_time), '-Q', QUARTER(change_time)) as timeKey , count(*) as totalCount, sum(case when sync_status = true then 1 else 0 end) as syncedCount, sum(case when sync_status = false then 1 else 0 end) as unsyncedCount, sum(case when change_type = 'object' then 1 else 0 end) as objectChangeCount, sum(case when change_type = 'field' then 1 else 0 end) as fieldChangeCount, sum(case when operation_type = 'create' then 1 else 0 end) as createCount, sum(case when operation_type = 'update' then 1 else 0 end) as updateCount, sum(case when operation_type = 'delete' then 1 else 0 end) as deleteCount, sum(case when is_custom = true then 1 else 0 end) as customCount, sum(case when is_custom = false then 1 else 0 end) as standardCount from datai_integration_metadata_change
and change_type = #{changeType} and operation_type = #{operationType} and object_api = #{objectApi} and sync_status = #{syncStatus} and is_custom = #{isCustom} and change_time >= #{startTime} and change_time <= #{endTime}
group by timeKey order by timeKey asc
