mapper.update:
update datai_integration_sync_log
batch_id = #{batchId}, object_api = #{objectApi}, record_id = #{recordId}, operation_type = #{operationType}, operation_status = #{operationStatus}, error_message = #{errorMessage}, execution_time = #{executionTime}, dept_id = #{deptId}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where datai_integration_sync_log.id = #{id}

mapper.select:
SELECT 
    COUNT(*) as totalCount,
    SUM(CASE WHEN operation_status = 'success' THEN 1 ELSE 0 END) as successCount,
    SUM(CASE WHEN operation_status = 'failure' THEN 1 ELSE 0 END) as failureCount,
    CASE WHEN COUNT(*) > 0 
        THEN ROUND(SUM(CASE WHEN operation_status = 'success' THEN 1 ELSE 0 END) * 100.0 / COUNT(*), 2)
        ELSE 0 
    END as successRate,
    ROUND(AVG(execution_time), 2) as avgExecutionTime,
    ROUND(MAX(execution_time), 2) as maxExecutionTime,
    ROUND(MIN(execution_time), 2) as minExecutionTime
FROM datai_integration_sync_log

    and batch_id = #{batchId}
    and object_api = #{objectApi}
    and operation_type = #{operationType}
    and operation_status = #{operationStatus}
    and create_time >= #{startTime}
    and create_time <= #{endTime}
    and dept_id = #{deptId}

SELECT 
    operation_type as operationType,
    COUNT(*) as count,
    SUM(CASE WHEN operation_status = 'success' THEN 1 ELSE 0 END) as successCount,
    SUM(CASE WHEN operation_status = 'failure' THEN 1 ELSE 0 END) as failureCount,
    ROUND(AVG(execution_time), 2) as avgExecutionTime
FROM datai_integration_sync_log

    and batch_id = #{batchId}
    and object_api = #{objectApi}
    and create_time >= #{startTime}
    and create_time <= #{endTime}
    and dept_id = #{deptId}

GROUP BY operation_type
ORDER BY count DESC

SELECT 
    object_api as objectApi,
    COUNT(*) as count,
    SUM(CASE WHEN operation_status = 'success' THEN 1 ELSE 0 END) as successCount,
    SUM(CASE WHEN operation_status = 'failure' THEN 1 ELSE 0 END) as failureCount,
    ROUND(AVG(execution_time), 2) as avgExecutionTime
FROM datai_integration_sync_log

    and batch_id = #{batchId}
    and operation_type = #{operationType}
    and create_time >= #{startTime}
    and create_time <= #{endTime}
    and dept_id = #{deptId}

GROUP BY object_api
ORDER BY count DESC

SELECT 
    operation_status as status,
    COUNT(*) as count
FROM datai_integration_sync_log

    and batch_id = #{batchId}
    and object_api = #{objectApi}
    and operation_type = #{operationType}
    and create_time >= #{startTime}
    and create_time <= #{endTime}
    and dept_id = #{deptId}

GROUP BY operation_status

SELECT 
    batch_id as batchId,
    COUNT(*) as count,
    SUM(CASE WHEN operation_status = 'success' THEN 1 ELSE 0 END) as successCount,
    SUM(CASE WHEN operation_status = 'failure' THEN 1 ELSE 0 END) as failureCount,
    ROUND(AVG(execution_time), 2) as avgExecutionTime
FROM datai_integration_sync_log

    and object_api = #{objectApi}
    and operation_type = #{operationType}
    and create_time >= #{startTime}
    and create_time <= #{endTime}
    and dept_id = #{deptId}

GROUP BY batch_id
ORDER BY batch_id DESC
LIMIT 10
